import jsonfrom odoo import httpclass WebsiteExtension(http.Controller):    @http.route('/change_brand_color', type='json', auth='user', csrf=False, website=True)    def change_brand_color(self):        print('HELLLLLLLO')        nav_color = http.request.env['ir.config_parameter'].sudo().get_param('website_extension.navbar_color')        menu_background_color = http.request.env['ir.config_parameter'].sudo().get_param(            'website_extension.menu_background_color')        data =  {            "nav_color": nav_color,            "menu_background_color": menu_background_color        }        return json.dumps(data)