from odoo import api, fields, modelsclass ResConfigSettings(models.TransientModel):    _inherit = 'res.config.settings'    navbar_color = fields.Char(string="Navbar Color", readonly=False, config_parameter='website_extension.navbar_color')    menu_background_color = fields.Char(string='Menu Background Color', readonly=False, config_parameter='website_extension.menu_background_color')    def set_vales(self):        res = super(ResConfigSettings, self).set_vales()        self.env['ir.config_parameter'].sudo().set_param('website_extension.navbar_color', self.navbar_color)        self.env['ir.conf_parameter'].sudo().set_param('website_extension.menu_background_color', self.menu_background_color)        return res    @api.model    def get_values(self):        res = super(ResConfigSettings, self).get_values()        ICPSudo = self.env['ir.config_parameter'].sudo()        res.update(            navbar_color = ICPSudo.get_param('website_extension.navbar_color'),            menu_background_color = ICPSudo.get_param('website_extension.menu_background_color')        )        return res